var WasService = require('./was-service'),
    AppService = require('./app-service');

exports.create = function(type) {
    switch (type) {
        case 1 :
            return new WasService();
        case 2 :
            return new AppService();
    }
    return null;
};

exports.WAS = 1;
exports.APP = 2;