exports.STMT = 0x0;
exports.PREPARED = 0x1;
exports.METHOD_KNOWN = 0x00;
exports.METHOD_EXECUTE = 0x10;
exports.METHOD_UPDATE = 0x20;
exports.METHOD_QUERY = 0x30;

exports.getClassType = function(xtype) {
    return (xtype & 0x0f);
};