var LinkedMap = require('./linkedmap');

function IntKeyLinkedMap(initCapacity, loadFactor) {
    LinkedMap.call(this, initCapacity, loadFactor);
}

IntKeyLinkedMap.prototype = new LinkedMap();
IntKeyLinkedMap.prototype.constructor = IntKeyLinkedMap;
IntKeyLinkedMap.prototype.put = function(key /*long*/, data /*Object*/){
    if((key instanceof Long) == false) {
        throw new Error('key must be Long object');
        return;
    }
    LinkedMap.prototype.put.call(this, key, data);
};
IntKeyLinkedMap.prototype.putLast = function(key, data){
    LinkedMap.prototype.putLast.call(this, key.toString(), data);
};
IntKeyLinkedMap.prototype.putFirst = function(key, data){
    LinkedMap.prototype.putFirst.call(this, key.toString(), data);
};
IntKeyLinkedMap.prototype.remove = function(key){
    LinkedMap.prototype.remove.call(this, key.toString());
};

module.exports = IntKeyLinkedMap;