require('./seedrandom');

var Long    = require('long'),
    rand    = new Math.seedrandom(Date.now(), {entropy : true});

var KeyGen = {
    next : function () {
        return Long.fromBits(rand.int32(), rand.int32());
    }
};

module.exports = KeyGen;