var NodeUtil = require('../util/nodeutil');
var emptyIp = new Buffer(4); emptyIp.fill(0);

function getIPAddresses() {

    var ipAddresses = [];
    var interfaces = require('os').networkInterfaces();
    for (var devName in interfaces) {
        var iface = interfaces[devName];
        for (var i = 0; i < iface.length; i++) {
            var alias = iface[i];
            if (alias.family === 'IPv4' && alias.address !== '127.0.0.1' && !alias.internal) {
                ipAddresses.push(alias.address);
            }
        }
    }
    return ipAddresses;
}
function getRandomInteger(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
}
function printWhatap() {
    var str =
        ' _      ____       ______NODE-AGENT  \n'+
        '| | /| / / /  ___ /_  __/__ ____' + '\n'+
        '| |/ |/ / _ \\/ _ `// / / _ `/ _ \\' + '\n'+
        '|__/|__/_//_/\\_,_//_/  \\_,_/ .__/' + '\n'+
        '    /_/' + '\n'+
        'Just Tap, Always Monitoring' + '\n'+
        'WhaTap Node Agent version ' + NodeUtil.getVersion() + ', ' + NodeUtil.getReleaseDate();
    console.log(str);
}

module.exports = {
    getIPAddresses: getIPAddresses,
    getRandomInteger: getRandomInteger,
    printWhatap: printWhatap
};